						   s=s.replaceAll("[0-9]", "");
						   s=s.replaceAll("nbsp;", "");
						   s=s.replaceAll("&quot;", "");
						   s=s.replaceAll("&;", "");
						   s=s.replaceAll("/", "");
						   s=s.replaceAll("=", "");
						   s=s.replaceAll(";", "");
						   s=s.replaceAll("-", "");
						   s=s.replaceAll(":", "");
						   s=s.replaceAll("\\(", "");
						   s=s.replaceAll("\\)", "");
						   s=s.replaceAll("\\.", "");
						   //s=s.replaceAll("\\s", "");
						String content=s;
						String regdate=String.valueOf(obj.get("reg_date"));
					    String poster="https://animalwa.org"+String.valueOf(obj.get("attach_file"));
